const express=require("express"),cors=require("cors"),app=express();app.use(cors()),app.use(express.json());const port=5e3,mysql=require("mysql"),connection=mysql.createConnection({host:"localhost",user:"root",password:"root"});connection.connect(),app.get("/bbs",(req,res)=>{connection.query("SELECT * from study.bbs",(function(err,rows,fields){if(err)throw err;res.send(rows),console.log(rows)}))}),app.delete("/bbs",(req,res)=>{const id=req.body.no;connection.query("delete from study.bbs where no=?",[id],(function(err,rows,fields){if(err)throw err;res.send(rows)}))}),app.post("/bbs",(req,res)=>{console.log("post");const{title:title,member:member,msg:msg}=req.body;console.log(req.body),connection.query("insert into study.bbs (title,member,msg) value(?,?,?)",[title,member,msg],(function(err,rows,fields){if(err)throw err;console.log(rows),connection.query("select * from study.bbs order by no desc limit 1 ",(function(err,rows,fields){if(err)throw err;res.send(rows),console.log(rows)}))}))}),app.listen(5e3,()=>{console.log("Example app listening at http://localhost:5000")});