const idval=document.querySelector("#userId"),pwval=document.querySelector("#userPw");document.querySelector("#loginBtn").addEventListener("click",()=>{""==idval.value?alert("아이디를 입력하세요"):""==pwval.value?alert("비밀번호 입력하세요"):fetch("http://localhost:5000/login",{method:"POST",headers:{"Content-Type":"application/json;charset=utf-8"},body:JSON.stringify({userId:idval.value,userPw:pwval.value})}).then(res=>res.json()).then(res=>{console.log(res),"success"==res.status?alert("성공"):alert("fail")})});